UPDATE public.booking
SET status = CASE
                 WHEN checkout_date <= CURRENT_DATE THEN 'CHECKED_OUT'
                 ELSE 'CHECKED_IN'
    END
WHERE checkin_date IS NOT NULL
  AND checkout_date IS NOT NULL
  AND (
    (checkout_date <= CURRENT_DATE AND status <> 'CHECKED_OUT') OR
    (checkout_date > CURRENT_DATE AND status <> 'CHECKED_IN')
    );